// Get all incoming items from supbase
const items = $input.all();

// Building a single object
return [
  {
    json: {
      id: items[0].json?.call_id || items[0].json?.chat_id,
      language:$input.first().json.language,
      created_at:$input.first().json.created_at,
      messages: items.map(i => ({
        msg: i.json.message || "",
        sender: i.json.sender || ""
      }))
    }
  }
];
